import React from 'react'

export const Form = () => {
  return (
    <div>
    <div class="container">
    <h1>FormSubmit Demo</h1>
    <form target="_blank" action="https://formsubmit.co/knupadhyay784@gmail.com" method="POST">
      <div class="form-group">
        <div class="form-row">
          <div class="col">
            <input type="text" name="name" class="form-control" placeholder="Full Name" required/>
          </div>
          <input type="hidden" name="_captcha" value="false"/>
          <div class="col">
            <input type="email" name="email" class="form-control" placeholder="Email Address" required/>
          </div>
          <div class="col">
          <input type="image" nam src="img_submit.gif" alt="Submit" width="48" height="48"/>
            {/* <input type="file" name="file" class="form-control" placeholder=""  accept="image/png, image/jpeg" required/> */}
          </div>
        </div>
      </div>
      <div class="form-group">
        <textarea placeholder="Your Message" class="form-control" name="message" rows="10" required></textarea>
      </div>
      <button type="submit" class="btn btn-lg btn-dark btn-block">Submit Form</button>
    </form>
  </div>
    </div>
  )
}
