import './App.css';
import ColorChange from './components/ColorChange';
import HeroSection from './components/HeroSection';
import Navbar from './components/Navbar';
import React, {useState} from 'react';


function App() {
  const [mode, setMode] = useState('light')
  // toggle1
  const toggle1 = () => {
    if (mode === 'light'){
      setMode('danger')
      document.body.style.backgroundColor = '#001f3f'
    }
    // else if (mode === 'dark') {
    else {
      setMode('danger')
      document.body.style.backgroundColor = '#001f3f'
    }

  }
//   //? - - - - - - - - - - - - - - - - - - - - - - - - - - - - 

  //* toggle 2
  const toggle2 = () => {
    if (mode === 'danger') {
      setMode('dark')
      document.body.style.backgroundColor = 'yellow'
    }
    // else if (mode === 'primary') {
    else {
      setMode('dark')
      document.body.style.backgroundColor = 'yellow'
    }
  }
//   //? - - - - - - - - - - - - - - - - - - - - - - - - - - - - 
  //* toggle 3
  const toggle3 = () => {
    if (mode === 'dark') {
      setMode('primary')
      document.body.style.backgroundColor = 'purple'
    }
    // else if (mode === 'warning') {
    else {
      setMode('primary')
      document.body.style.backgroundColor = 'purple'
    }
  }
  //? - - - - - - - - - - - - - - - - - - - - - - - - - - - - 

  //* toggle 4
  const toggle4 = () => {
    if (mode === 'primary') {
      setMode('warning')
      document.body.style.backgroundColor = 'blue'
    }
    else {
      setMode('warning')
      document.body.style.backgroundColor = 'blue'
    }
  }

  //? - - - - - - - - - - - - - - - - - - - - - - - - - - - - 
//* toggle 5
const toggle5 = () => {
  if (mode === 'warning') {
    setMode('secondary')
    document.body.style.backgroundColor = 'green'
  }
  else {
    setMode('secondary')
    document.body.style.backgroundColor = 'blue'
  }
}
//? - - - - - - - - - - - - - - - - - - - - - - - - - - - - 
  return (
    <>
     <Navbar title='कमल नयन' mode={mode} toggle1={toggle1} toggle2={toggle2} toggle3={toggle3} toggle4={toggle4} toggle5={toggle5} />
      <HeroSection mode={mode} />
      <ColorChange mode={mode} toggle1={toggle1} toggle2={toggle2} toggle3={toggle3} toggle4={toggle4} toggle5={toggle5} />
    </>
  );
}

export default App;
